import React, { useState } from 'react';
import axiosInstance from './axiosinstance';
import './adduserprofile';

function UserProfile() {
    const [userData, setUserData] = useState({
        id: '',
        username: '',
        name: '',
        email: '',
        balance: '',
        salary: '',
        rent: ''
    });
    const [message, setMessage] = useState('');

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setUserData({ ...userData, [name]: value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axiosInstance.post('/Updateuserprofile', userData);
            setMessage(`User updated successfully: ${response.data.name}`);
        } catch (error) {
            console.error("Error updating user profile", error);
            setMessage("Failed to update user profile. User not found or invalid data.");
        }
    };
    return (
        <div className="tab-section">
            <h2>User Profile Management</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    User ID: 
                    <input 
                        type="number" 
                        name="id" 
                        placeholder="Enter user ID" 
                        value={userData.id} 
                        onChange={handleInputChange} 
                        required 
                    />
                </label>
                <br />
                <label>
                    Username:
                    <input 
                        type="text" 
                        name="username" 
                        placeholder="Enter your username" 
                        value={userData.username} 
                        onChange={handleInputChange} 
                        required 
                    />
                </label>
                <br />
                <label>
                    Name:
                    <input 
                        type="text" 
                        name="name" 
                        placeholder="Enter your name" 
                        value={userData.name} 
                        onChange={handleInputChange} 
                    />
                </label>
                <br />
                <label>
                    Email:
                    <input 
                        type="email" 
                        name="email" 
                        placeholder="Enter your email" 
                        value={userData.email} 
                        onChange={handleInputChange} 
                    />
                </label>
                <br />
                <label>
                    Balance:
                    <input 
                        type="number" 
                        name="balance" 
                        placeholder="Enter your balance" 
                        value={userData.balance} 
                        onChange={handleInputChange} 
                    />
                </label>
                <br />
                <label>
                    Salary:
                    <input 
                        type="number" 
                        name="salary" 
                        placeholder="Enter your salary" 
                        value={userData.salary} 
                        onChange={handleInputChange} 
                    />
                </label>
                <br />
                <label>
                    Rent:
                    <input 
                        type="number" 
                        name="rent" 
                        placeholder="Enter your rent" 
                        value={userData.rent} 
                        onChange={handleInputChange} 
                    />
                </label>
                <br />
                <button type="submit">Update Profile</button>
            </form>
            {message && <p>{message}</p>}
        </div>
    );
}

export default UserProfile;
