// CreditCardApplication.js
import React, { useState } from 'react';
import axios from 'axios';

function CreditCardApplication() {
    const [cardType, setCardType] = useState('Visa');
    const [creditLimit, setCreditLimit] = useState('');
    const [rent, setRent] = useState('');
    const [age, setAge] = useState('');
    const [salary, setSalary] = useState('');
    const [approvalStatus, setApprovalStatus] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();

        // Create an object with the data to send
        const applicationData = {
            cardType,
            creditLimit,
            rent,
            age,
            salary,
        };

        try {
            // Replace with your actual API endpoint
            const response = await axios.post('http://localhost:8080/api/credit-card-application', applicationData);
            setApprovalStatus(response.data.message); // Adjust based on your API response structure
        } catch (error) {
            console.error('Error submitting application:', error);
            setApprovalStatus('Error submitting application. Please try again later.');
        }
    };

    return (
        <div className="tab-section">
            <h2>Credit Card Application</h2>
            <form onSubmit={handleSubmit}>
                <label>
                    Card Type: 
                    <select value={cardType} onChange={(e) => setCardType(e.target.value)}>
                        <option value="Visa">Visa</option>
                        <option value="MasterCard">MasterCard</option>
                    </select>
                </label><br />
                <label>
                    Credit Limit: 
                    <input 
                        type="number" 
                        placeholder="Desired limit" 
                        value={creditLimit}
                        onChange={(e) => setCreditLimit(e.target.value)}
                    />
                </label><br />
                <label>
                    Rent: 
                    <input 
                        type="number" 
                        placeholder="Monthly Rent" 
                        value={rent}
                        onChange={(e) => setRent(e.target.value)}
                    />
                </label><br />
                <label>
                    Age: 
                    <input 
                        type="number" 
                        placeholder="Your Age" 
                        value={age}
                        onChange={(e) => setAge(e.target.value)}
                    />
                </label><br />
                <label>
                    Salary: 
                    <input 
                        type="number" 
                        placeholder="Monthly Salary" 
                        value={salary}
                        onChange={(e) => setSalary(e.target.value)}
                    />
                </label><br />
                <button type="submit">Apply</button>
            </form>
            <p>{approvalStatus}</p>
        </div>
    );
}

export default CreditCardApplication;
